import 'next-auth'

declare module 'next-auth' {
  interface User {
    id: string
    role: string
    candidateProfileId?: string
    employerProfileId?: string
  }

  interface Session {
    user: {
      id: string
      email: string
      role: string
      candidateProfileId?: string
      employerProfileId?: string
    }
  }
}

declare module 'next-auth/jwt' {
  interface JWT {
    id: string
    role: string
    candidateProfileId?: string
    employerProfileId?: string
  }
}

